window.onload = function canvas() {

    var chart = new CanvasJS.Chart("chartContainer", {
        theme: "light2", // "light1", "light2", "dark1", "dark2"
        animationEnabled: true,
        title:{
            text: "Weather"   
        },
        axisX: {
            interval: 3,
            intervalType: "hour",
            valueFormatString: "HH:MM"
        },
        axisY:{
            title: "Temperature"
        },
        data: [{        
            type: "line",
            markerSize: 15,
            xValueFormatString: "MMM, YYYY",
            yValueFormatString: "$###.#",
            dataPoints: [        
                { x: new Date(2016, 01, 1, 00, 00), y: 12, indexLabel: "Clear" , markerType: "circle",  markerColor: "#6B8E23" },
                { x: new Date(2016, 01, 1, 03, 00), y: 12, indexLabel: "Clear", markerType: "circle",  markerColor: "#6B8E23" },
                { x: new Date(2016, 01, 1, 06, 00) , y: 13, indexLabel: "Clear", markerType: "circle", markerColor: "#6B8E23" },
                { x: new Date(2016, 01, 1, 09, 00) , y: 18, indexLabel: "Cloudy", markerType: "circle", markerColor: "grey" },
                { x: new Date(2016, 01, 1, 12, 00) , y: 21, indexLabel: "Cloudy", markerType: "circle", markerColor: "grey" },
                { x: new Date(2016, 01, 1, 15, 00) , y: 20, indexLabel: "Rain", markerType: "circle", markerColor: "#0000FF" },
                { x: new Date(2016, 01, 1, 18, 00) , y: 19, indexLabel: "Rain", markerType: "circle", markerColor: "#0000FF" },
                { x: new Date(2016, 01, 1, 20, 00) , y: 18, indexLabel: "Clear", markerType: "circle", markerColor: "#6B8E23" },
                { x: new Date(2016, 01, 1, 23, 00) , y: 13, indexLabel: "Clear", markerType: "circle", markerColor: "#6B8E23" },
            
            ]
        }]
    });
    chart.render();
    
    }